import React, { Component } from 'react';
import styled from 'styled-components';
import {ProdcutConsumer, ProductConsumer} from '../context';
import {ButtonContainer} from './Button';
import {Link} from 'react-router-dom';

export default class Modal extends Component {
    render() {
        return (
           <ProductConsumer>
           {(value) => {
            const {modalOpen,closeModal} = value;
            const {img,title,price} = value.modalProduct;
            if(!modalOpen){
                return null;
            }   
            else {
               return( <ModalContanier>
                


    <div className="modal fade" id="basicExampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel"
  aria-hidden="true">
  <div className="modal-dialog" role="document">
    <div className="modal-content">
      <div className="modal-header">
        <h5 className="modal-title" id="exampleModalLabel">item added to the cart</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="modal-body">
      <img src={img} className="img-fluid" alt="product" />
      <h5>{title}</h5>
      <h5 className="text-muted">price : $ {price}</h5>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Save changes</button>

        <Link to="/">
        <ButtonContainer onClick={()=> closeModal()}>
            Continue Shopping
        </ButtonContainer>
        </Link>

        <Link to="/carts">
        <ButtonContainer onClick={()=> closeModal()}>
            go to cart
        </ButtonContainer>
        </Link>
        
      </div>
      
    </div>
  </div>
</div>

                </ModalContanier>
               );
            }
           }}
           </ProductConsumer>
        );
    }
}

const ModalContanier = styled.div`
position:fixed;
top:0;
left:0;
bottom:0;
background:rgba(0,0,0,0.3);
display:flex;
align-items:center;
justify-content:center;
#modal{
    background: var(--mainWhite);
}
`;
