import React, { Component } from 'react';
import styled from 'styled-components';
import {Link} from 'react-router-dom';
import {ProductConsumer} from '../context';
import {ButtonContainer} from './Button';

export default class Modal extends Component {
    render() {
        return (
           <ProductConsumer>
           {(value) => {
            const {modalOpen,closeModal} = value;
            const {img,title,price} = value.modalProduct;
            if(!modalOpen){
                return null;
            }   
            else {
               return( 
        <ModalContanier>

        <button type="button" className="btn btn-primary" data-toggle="modal" data-target="#basicExampleModal">
  Launch demo modal
</button>

<div className="modal fade" id="basicExampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel"
  aria-hidden="true">
  <div className="modal-dialog" role="document">
    <div className="modal-content">
      <div className="modal-header">
        <h5 className="modal-title" id="exampleModalLabel">Modal title</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="modal-body">
        ...
      </div>
      <div class="modal-footer">
        <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
        <button type="button" className="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>

    <div className="container">
  <div className="row">
  
    <div id="modal" className="col-8 mx-auto col-md-6 col-lg-4 text-center text-capitalize p-5">
     
        <h5>item added to the cart</h5>
       
      <img src={img} className="img-fluid" alt="product" />
      <h5>{title}</h5>
      <h5 className="text-muted">price : $ {price}</h5>
      
        <Link to="/">
        <ButtonContainer onClick={()=> closeModal()}>
            store
        </ButtonContainer>
        </Link>

        <Link to="/cart">
        <ButtonContainer cart onClick={()=> closeModal()}>
            go to cart
        </ButtonContainer>
        </Link>
      
    </div>
  </div>
</div>

                </ModalContanier>
               );
            }
           }}
           </ProductConsumer>
        );
    }
}

const ModalContanier = styled.div`
position:fixed;
top:0;
left:0;
bottom:0;
background:rgba(0,0,0,0.3);
display:flex;
align-items:center;
justify-content:center;
#modal{
    background: var(--mainWhite);
}
`;
